import React, { useState, useEffect } from "react";
import Services from "./Services";
import { useLocation } from "react-router-dom";

function Home() {

    const location = useLocation();

    const [formData, setFormData] = useState({
        name: "",
        mobile: "",
        email: "",
        services: [],
        documents: [],
    });

    useEffect(() => {
        if (location.state?.selectedService) {
            setFormData((prevData) => ({
                ...prevData,
                services: [location.state.selectedService],
            }));
        }
    }, [location.state]);

    const [message, setMessage] = useState("");

    // Available services
    const availableServices = [
        "Jaati Praman Patra",
        "Nikaas Praman Patra",
        "Aay Praman Patra",
        "Pension Form",
        "Jivan Praman Patra",
        "PAN Card",
        "Ration Card",
        "Rojgar Panjikaran",
        "Online Form",
        "Photo State Colour",
        "Aadhar Card Print",
        "Aayushman Card",
        "Dukaanon ka Panjikaran",
        "Khasra Khatauni Nakal",
        "Pahchan Patra",
        "Vahan Bima",
        "Passport Photo",
        "Charitra Praman Patra",
        "PM Kisan Yojana",
        "Driving Licence",
        "Shramik Panjikaran",
        "Bijali Bill",
        "Passport",
        "PF Withdrawal",
        "A3 Colour Print",
    ];

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleServiceChange = (e) => {
        const { options } = e.target;
        const selectedServices = [];
        for (const option of options) {
            if (option.selected) {
                selectedServices.push(option.value);
            }
        }
        setFormData({ ...formData, services: selectedServices });
    };

    const handleFileChange = (e) => {
        setFormData({ ...formData, documents: [...e.target.files] });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Create a FormData object to send the data
        const formPayload = new FormData();
        formPayload.append("name", formData.name);
        formPayload.append("mobile", formData.mobile);
        formPayload.append("email", formData.email);
        formPayload.append("services", formData.services.join(", "));
        formData.documents.forEach((doc, idx) => {
            formPayload.append(`document-${idx + 1}`, doc);
        });

        try {
            // Send form data to backend (use your backend endpoint)
            const response = await fetch("https://your-backend-endpoint/send-email", {
                method: "POST",
                body: formPayload,
            });

            if (response.ok) {
                setMessage("Form submitted successfully!");
            } else {
                setMessage("Failed to submit the form. Please try again.");
            }
        } catch (error) {
            console.error("Error submitting form:", error);
            setMessage("An error occurred. Please try again.");
        }
    };

    return (
        <main className="bg-gray-100 min-h-screen">
            <div className="container mx-auto p-6">
                {/* Two Sections */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                    {/* Section 1: Customer Search */}
                    <section className="bg-white shadow-md rounded-lg p-6 flex">
                        <div className="container mx-auto my-auto px-4 text-center">
                            <h1 className="text-4xl font-bold text-blue-600">Welcome to Jan Seva Kendra</h1>
                            <p className="text-gray-700 mt-4 text-lg">
                                Your one-stop destination for government and utility services.
                            </p>
                            <button className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 mt-10" onClick={Services}>
                                Explore Services
                            </button>
                        </div>
                    </section>

                    {/* Section 2: Labor Registration */}
                    <section className="bg-white shadow-md rounded-lg p-6">
                        <h2 className="text-3xl font-bold text-blue-600 mb-6 text-center">
                            Request Our Services
                        </h2>
                        {message && (
                            <div className="text-center mb-4 text-green-600">{message}</div>
                        )}
                        <form onSubmit={handleSubmit} className="space-y-6">
                            <div>
                                <label
                                    htmlFor="name"
                                    className="block text-gray-700 font-medium mb-2"
                                >
                                    Name
                                </label>
                                <input
                                    type="text"
                                    id="name"
                                    name="name"
                                    className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:ring-2 focus:ring-blue-600 outline-none"
                                    value={formData.name}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div>
                                <label
                                    htmlFor="mobile"
                                    className="block text-gray-700 font-medium mb-2"
                                >
                                    Mobile No.
                                </label>
                                <input
                                    type="tel"
                                    id="mobile"
                                    name="mobile"
                                    className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:ring-2 focus:ring-blue-600 outline-none"
                                    value={formData.mobile}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div>
                                <label
                                    htmlFor="email"
                                    className="block text-gray-700 font-medium mb-2"
                                >
                                    Email
                                </label>
                                <input
                                    type="email"
                                    id="email"
                                    name="email"
                                    className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:ring-2 focus:ring-blue-600 outline-none"
                                    value={formData.email}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div>
                                <label
                                    htmlFor="services"
                                    className="block text-gray-700 font-medium mb-2"
                                >
                                    Select Services
                                </label>
                                <select
                                    id="services"
                                    name="services"
                                    multiple
                                    className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:ring-2 focus:ring-blue-600 outline-none"
                                    onChange={handleServiceChange}
                                    required
                                >
                                    {availableServices.map((service, index) => (
                                        <option key={index} value={service}>
                                            {service}
                                        </option>
                                    ))}
                                </select>
                            </div>
                            <div>
                                <label
                                    htmlFor="documents"
                                    className="block text-gray-700 font-medium mb-2"
                                >
                                    Upload Documents
                                </label>
                                <input
                                    type="file"
                                    id="documents"
                                    name="documents"
                                    multiple
                                    className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:ring-2 focus:ring-blue-600 outline-none"
                                    onChange={handleFileChange}
                                />
                            </div>
                            <button
                                type="submit"
                                className="bg-blue-600 text-white font-medium px-6 py-2 rounded-lg hover:bg-blue-700 transition-all"
                            >
                                Submit
                            </button>
                        </form>
                    </section>
                </div>
            </div>
        </main>
    );
}

export default Home;
